import React, { useRef, useState } from 'react'
import axios from '../api/axios';

const FORGET_PASSWORD = "/forget-password"

const ForgetPassword = () => {
    const emailRef = useRef();

    const [email, setEmail] = useState('');

    const handleSubmit = async()=>{
        try {
            const response = await axios.post(FORGET_PASSWORD, { "email": email });

            alert(response.data.message);
            navigate(from, { replace: true });
            setCurrentComponent(<Task />)
        } catch (err) {
            if (!err?.response) {
                setErrMsg('No server response');
            }
            else if (err.response?.status === 400) {
                setErrMsg('Please create a task...');
            } else if (err.response?.status === 401) {
                setErrMsg('Unautherized');
            }
            else {
                setErrMsg('You are not login, Login first...');
            }

            // errRef.current.focus();
        }
    }

  return (
    <>
       <section className="vh-100 style-1" >
            <div className="container py-5 h-100">
                {/* { errMsg ? <p>{errMsg}</p> : ''} */}
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                        <div className="card shadow-2-strong style-2">
                            <form className="card-body p-5 text-center" onSubmit={handleSubmit}>

                                <h3 className="mb-5"></h3>

                                <div className="form-outline mb-4">

                                    <label
                                        htmlFor="typeEmailX-2"
                                        className="form-label">
                                        Email
                                    </label>
                                    <input
                                        id="typeEmailX-2"
                                        className="form-control form-control-lg"
                                        type="email"
                                        name="email"
                                        ref={emailRef}
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>

                                <button className="btn btn-primary btn-lg btn-block" type="submit" >click here</button>

                                {/* <hr className="my-4" />

                                <button className="btn btn-lg btn-block btn-primary style-3"
                                    type="submit"><i className="fab fa-google me-2"></i> Sign in with google</button>
                                <button className="btn btn-lg btn-block btn-primary mb-2 style-4"
                                    type="submit"><i className="fab fa-facebook-f me-2"></i>Sign in with facebook</button> */}

                                <p className="text-center text-muted mt-4 mb-0">New  account? <a href="/register"
                                    className="fw-bold text-body">Register here</a></p>

                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    </>
  )
}

export default ForgetPassword
